INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (1, 'Blonde', 'Bière légère, dorée et généralement peu amère. Elle est facile à boire avec des arômes doux, souvent floraux ou fruités.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (2, 'Brune', 'Bière de couleur foncée, allant du marron au noir, avec des saveurs riches de caramel, de café, de chocolat ou de fruits secs.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (3, 'Triple (ou Tripel)', 'Bière belge forte, brassée avec une quantité plus importante de malt, ce qui lui donne une teneur en alcool plus élevée (généralement entre 7 et 10%). Elle est souvent épicée et fruitée.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (4, 'Blanche', 'Bière brassée avec du blé, souvent trouble et rafraîchissante, avec des notes d’agrumes et d''épices (comme la coriandre ou le zeste d’orange).');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (5, 'Rouge', 'Bière qui a souvent une teinte rougeâtre et peut avoir des arômes fruités. Certaines bières rouges sont obtenues par l’ajout de fruits rouges, mais cela peut aussi être lié au malt utilisé.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (6, 'Fruitée', 'Bière à laquelle on ajoute des fruits pour lui donner une saveur sucrée et fruitée. Elle est populaire dans les bières belges, comme les Lambics aromatisés (cerise, framboise, etc.).');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (7, 'IPA (India Pale Ale)', 'Bière houblonnée avec une amertume plus prononcée. L''IPA est souvent plus fruitée, florale et parfois résineuse selon le type de houblon utilisé.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (8, 'Ambrée', 'Bière de couleur ambrée à rougeâtre, souvent légèrement plus maltée que les blondes, avec des saveurs de caramel et de fruits secs.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (9, 'Blonde d''Abbaye', 'Bière blonde souvent brassée par des abbayes ou selon des traditions monastiques. Elle est plus forte en alcool et a des saveurs complexes de fruits et d''épices.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (10, 'Bière de Noël', 'Bière souvent brassée pour les fêtes de fin d''année, plus épicée et plus riche, avec des arômes de cannelle, de clou de girofle ou d''autres épices de saison.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (11, 'Saison', 'Bière rustique originaire de Belgique, brassée autrefois pour les travailleurs agricoles. Elle est légèrement épicée, souvent acidulée, et rafraîchissante.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (12, 'Gose', 'Bière légère et acidulée, originaire d''Allemagne, souvent brassée avec du sel et de la coriandre. Elle a une légère salinité qui la rend unique.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (13, 'Imperial Stout', 'Une version plus forte et plus alcoolisée du stout. Ces bières sont riches en saveurs torréfiées comme le chocolat noir, le café et parfois des notes de fruits noirs.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (14, 'Quadrupel', 'Bière belge très forte, souvent autour de 10-12% d''alcool. Elle est complexe, avec des notes de fruits secs, de caramel, de sucre brun et parfois de chocolat.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (15, 'Barleywine (Vin d''orge)', 'Bière très forte, souvent proche du vin en termes de teneur en alcool (10-12%). Elle a des saveurs riches de malt, avec des notes de fruits secs, de caramel et parfois de houblon.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (16, 'Double (ou Dubbel)', 'Bière belge plus maltée et légèrement sucrée, souvent brune, avec des saveurs de fruits mûrs et de caramel. Elle est plus douce que la Triple.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (17, 'Pils (ou Pilsner)', 'Bière légère, claire et rafraîchissante, avec une amertume modérée. C''est l''une des bières les plus consommées au monde, souvent désignée simplement comme "pils".');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (18, 'Session IPA', 'Version plus légère et moins alcoolisée de l''IPA. Elle conserve les arômes houblonnés mais est plus facile à boire en grande quantité.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (19, 'Lager', 'Catégorie générale pour les bières de fermentation basse. Souvent claires et légères, les lagers incluent des sous-types comme la Pilsner, mais peuvent aussi être plus maltées ou ambrées.');
INSERT INTO Beer_Types (type_id, type_name, type_description) VALUES (20, 'Faro', 'Bière belge de type Lambic, mais sucrée avec du sucre candi, offrant un goût sucré-acidulé. Elle est moins courante mais appréciée pour sa douceur.');